import React from 'react';
import { useLazyLoading } from '../hooks/useLazyLoading';

interface LazyImageProps {
  src: string;
  alt: string;
  className?: string;
  placeholder?: string;
  onLoad?: () => void;
  onError?: () => void;
}

const LazyImage: React.FC<LazyImageProps> = ({
  src,
  alt,
  className = '',
  placeholder = 'data:image/svg+xml;base64,',
  onLoad,
  onError
}) => {
  const { elementRef, isIntersecting } = useLazyLoading();

  return (
    <img
      ref={elementRef}
      src={isIntersecting ? src : placeholder}
      alt={alt}
      className={`transition-opacity duration-300 ${isIntersecting ? 'opacity-100' : 'opacity-70'} ${className}`}
      loading="lazy"
      onLoad={onLoad}
      onError={onError}
    />
  );
};

export default LazyImage; 